  }

  handleButtonClick = e => {
    e.preventDefault();
    counter++;
  };

  secondHandleButtonClick = e => {
    e.preventDefault();
    counter--;
  };

  render() {
    return (
      <div>
        <h4>{this.state.stuff}</h4>
        <button onClick={this.handleButtonClick}>Click Me</button>
        <button onClick={this.secondHandleButtonClick}>Dont Click Me</button>
      </div>
    );
  }
}


class App extends React.Component {
  render() {
    return (
      <React.Fragment>
        <Header />
        <Counter />
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
